window.RFTCookies=class{setCookie(e,t,o){var n="";if(o){var i=new Date;i.setTime(i.getTime()+24*o*60*60*1e3),n="; expires="+i.toUTCString()}document.cookie=e+"="+(t||"")+n+"; path=/"}getCookie(e){for(var t=e+"=",o=document.cookie.split(";"),n=0;n<o.length;n++){for(var i=o[n];" "==i.charAt(0);)i=i.substring(1,i.length);if(0==i.indexOf(t))return i.substring(t.length,i.length)}return null}setPolkadotConnected(){this.setCookie("signer","polkadot",365)}setMetamaskConnected(){this.setCookie("signer","metamask",365)}async getConnectedSigner(){return this.getCookie("signer")}};